import React from 'react';
import {Grid, Text, Input, Button, Image} from '../elements';
import Upload from "../shared/Upload";

import {useSelector, useDispatch} from "react-redux"
import {actionCreators as postActions} from '../redux/modules/post';

const PostWrite = (props) => {
    const dispatch = useDispatch();
    const is_login = useSelector((state) => state.user.is_login);
    const preview = useSelector((state) => state.image.preview);
    const post_list = useSelector((state) => state.post.list);


    const post_id = props.match.params.id;
    const is_edit = post_id ? true : false;

    const {history} = props;

    const [contents, setContents] = React.useState();

    const changeContents = (e) => {
        setContents(e.target.value);
    }

    const addPost = () => {
        dispatch(postActions.addPostFB(contents));
    }

    if (!is_login) {
        return (
            <Grid margin="100px 0px" padding="16px" center="center">
                <Text size="32px" bold="bold">앗! 잠깐!</Text>
                <Text size="16px">로그인 후에만 글을 쓸 수 있어요!</Text>
                <Button
                    _onClick={() => {
                        history.replace("/")
                    }}>로그인 하러가기</Button>
            </Grid>
        )
    };

    return (
        <React.Fragment>
            <Grid padding="16px">
                <Text margin="0px" size="36px" bold="bold">
                    게시글 작성
                </Text>
                <Upload/>
            </Grid>
            <Grid>
                <Grid padding="16px">
                    <Text margin="0px" size="24px" bold="bold">미리보기</Text>
                </Grid>
                <Image
                    shape="rectangle"
                    src={preview
                        ? preview
                        : "http://via.placeholder.com/400x300"}/>
            </Grid>
            <Grid padding="16px">
                <Input
                    _onChange={changeContents}
                    label="게시글내용"
                    placeholder="게시글 작성"
                    multiLine="multiLine"/>
            </Grid>
            <Grid padding="16px">
                <Button text="게시글 작성" _onClick={addPost}></Button>
            </Grid>
        </React.Fragment>
    )
}

export default PostWrite;
